import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/OrderTracking.css'; 
const OrderTracking = () => {
    // Order status data with stages 3 and 4 removed
    const orderStatus = [
        { id: 1, status: 'Order Placed' },
        { id: 2, status: 'Preparing' }
    ];

    const [showCracker, setShowCracker] = useState(false);

    useEffect(() => {
        // Show the cracker animation for a short time when the component mounts
        setShowCracker(true);
        const timer = setTimeout(() => setShowCracker(false), 3000); // Hide after 3 seconds
        return () => clearTimeout(timer);
    }, []);

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <div className="card bg-transparent border-0">
                <div className="card-body">
                    <h1 className="text-center display-4 mb-4 text-black">Order Tracking</h1>
                    <ul className="list-group">
                        {orderStatus.map((status) => (
                            <li
                                key={status.id}
                                className="list-group-item d-flex justify-content-center align-items-center bg-light mb-3"
                                style={{ borderRadius: '20px', position: 'relative' }}
                            >
                                <span className="fw-bold">{status.status}</span>
                                {/* Cracker animation */}
                                {status.id === 1 && showCracker && (
                                    <div className="cracker-animation">
                                        <div className="cracker"></div>
                                        <div className="cracker"></div>
                                        <div className="cracker"></div>
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                    <div className="d-flex justify-content-center mt-4">
                        <Link to="/" className="btn btn-outline-light text-black" style={{ borderRadius: '20px' }}>Back to Home</Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default OrderTracking;
