import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const ItemSelection = () => {
    const { id } = useParams();
    const item = restaurants.flatMap((r) => r.menu).find((i) => i.id === parseInt(id));
    const [quantity, setQuantity] = useState(1);

    const handleChange = (e) => setQuantity(e.target.value);

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Select Item</h1>
            <h2>{item.name} - â‚¹{item.price}</h2>
            <p>{item.description}</p>
            <label className="block mt-4">Quantity:
                <input
                    type="number"
                    value={quantity}
                    onChange={handleChange}
                    className="ml-2 p-1 border"
                    min="1"
                />
            </label>
            <Link to="/order" className="text-blue-500  mt-4 block">Place Order</Link>
        </div>
    );
};

export default ItemSelection;
