import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const Menu = () => {
    const { id } = useParams();
    const restaurant = restaurants.find((r) => r.id === parseInt(id));

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">{restaurant.name} - Menu</h1>
            <ul>
                {restaurant.menu.map((item) => (
                    <li key={item.id} className="mb-2">
                        <h2 className="font-bold">{item.name} - â‚¹{item.price}</h2>
                        <p>{item.description}</p>
                        <Link to={`/select/${item.id}`} className="text-blue-500 underline">
                            Select
                        </Link>
                    </li>
                ))}
            </ul>
            <Link to="/" className="text-blue-500 underline mt-4 block">Back to Restaurants</Link>
        </div>
    );
};

export default Menu;
