import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const Menu = () => {
    const { id } = useParams();
    const restaurant = restaurants.find((r) => r.id === parseInt(id));

    return (
        <div className="p-4 bg-gray-100 min-h-screen">
            <h1 className="text-3xl font-extrabold text-gray-800 mb-6">{restaurant.name} - Menu</h1>
            <ul className="space-y-6">
                {restaurant.menu.map((item) => (
                    <li key={item.id} className="p-4 bg-white shadow-lg rounded-lg">
                        <h2 className="text-xl font-semibold text-gray-900 mb-2">
                            {item.name} - <span className="text-green-500">â‚¹{item.price}</span>
                        </h2>
                        <p className="text-gray-700 mb-4">{item.description}</p>
                        <Link
                            to={`/select/${item.id}`}
                            className="inline-block bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
                        >
                            Select
                        </Link>
                    </li>
                ))}
            </ul>
            <Link
                to="/"
                className="inline-block mt-8 text-blue-500 hover:text-blue-600 underline"
            >
                Back to Restaurants
            </Link>
        </div>
    );
};

export default Menu;
