import React from 'react';
import { useLocation, Link } from 'react-router-dom';

const PlaceOrder = () => {
    const location = useLocation();
    const item = location.state?.item; // Access the item from state

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <h1 className="text-center display-4 mb-5 text-black">Place Your Order</h1>
            <div className="card bg-transparent border-0 text-black">
                <div className="card-body">
                    <h2 className="h4 card-title">Order Summary</h2>
                    {item ? (
                        <div>
                            <h3 className="h5">{item.name} - â‚¹{item.price}</h3>
                            <p>{item.description}</p>
                        </div>
                    ) : (
                        <p>Your selected items will appear here.</p>
                    )}
                    <div className="mt-4">
                        <Link to="/track" className="btn btn-light btn-block text-black">
                            Confirm Order
                        </Link>
                    </div>
                    <div className="mt-3">
                        <Link to={`/menu/${item?.restaurantId}`} className="btn btn-outline-light btn-block text-black">
                            Back to Menu
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PlaceOrder;
