import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const ItemSelection = () => {
    const { id } = useParams();
    const restaurant = restaurants.find((r) => r.menu.some((item) => item.id === parseInt(id)));
    const selectedItem = restaurant.menu.find((item) => item.id === parseInt(id));

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <h1 className="text-center display-4 mb-5 text-black">Confirm Your Selection</h1>
            <div className="card bg-transparent border-0">
                <div className="card-body">
                    <h2 className="card-title text-white">{selectedItem.name} - â‚¹{selectedItem.price}</h2>
                    <p className="card-text text-white">{selectedItem.description}</p>
                    <div className="d-flex justify-content-center mt-4">
                        <Link to="/place-order" className="btn btn-outline-light me-2">Place Order</Link>
                        <Link to={`/menu/${restaurant.id}`} className="btn btn-light">Back to Menu</Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ItemSelection;
