import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const PlaceOrder = () => {
    const [cart, setCart] = useState([]);
    const [restaurantId, setRestaurantId] = useState(null);

    useEffect(() => {
        // Load cart from local storage on component mount
        const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
        setCart(storedCart);

        // Set the restaurant ID from the first item in the cart
        if (storedCart.length > 0) {
            setRestaurantId(storedCart[0].restaurantId);
        }
    }, []);

    const handleDelete = (index) => {
        const updatedCart = cart.filter((_, i) => i !== index);
        setCart(updatedCart);
        localStorage.setItem('cart', JSON.stringify(updatedCart));

        // Update restaurantId if cart becomes empty
        if (updatedCart.length === 0) {
            setRestaurantId(null);
        }
    };

    const handleConfirmOrder = () => {
        // Clear the cart after confirming the order
        localStorage.removeItem('cart');
    };

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <h1 className="text-center display-4 mb-5 text-black">Place Your Order</h1>
            <div className="card bg-transparent border-0 text-black">
                <div className="card-body">
                    <h2 className="h4 card-title">Order Summary</h2>
                    {cart.length > 0 ? (
                        <div>
                            {/* ... (existing cart item rendering code) ... */}
                            <div className="mt-4">
                                <Link
                                    to="/track"
                                    onClick={handleConfirmOrder}
                                    className="btn btn-light btn-block text-black me-2"
                                    style={{ borderRadius: '20px' }}
                                >
                                    Confirm Order
                                </Link>
                                {restaurantId && (
                                    <Link 
                                        to={`/menu/${restaurantId}`} 
                                        className="btn btn-outline-light text-black"
                                        style={{ borderRadius: '20px' }}
                                    >
                                        Back to Menu
                                    </Link>
                                )}
                            </div>
                        </div>
                    ) : (
                        <p>No items in the cart.</p>
                    )}
                    <div className="mt-3">
                        <Link to="/" className="btn btn-outline-light btn-block text-black" style={{ borderRadius: '20px' }}>
                            Back to Home
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PlaceOrder;
