import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const ItemSelection = () => {
    const { id } = useParams();
    const item = restaurants.flatMap((r) => r.menu).find((i) => i.id === parseInt(id));
    const [quantity, setQuantity] = useState(1);

    const handleChange = (e) => setQuantity(e.target.value);

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <div className="card bg-transparent border-0">
                <div className="card-body">
                    <h1 className="text-center display-4 mb-4 text-white">Select Item</h1>
                    <h2 className="card-title text-white mb-3">{item.name} - â‚¹{item.price}</h2>
                    <p className="card-text text-white mb-4">{item.description}</p>
                    <div className="mb-4">
                        <label className="form-label text-white">Quantity:
                            <input
                                type="number"
                                value={quantity}
                                onChange={handleChange}
                                className="form-control mt-2"
                                min="1"
                            />
                        </label>
                    </div>
                    <div className="d-flex justify-content-center">
                        <Link to="/order" className="btn btn-outline-light me-2">Place Order</Link>
                        <Link to={`/menu/${item.restaurantId}`} className="btn btn-light">Back to Menu</Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ItemSelection;
