import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { restaurants } from '../data/restaurants';

const PlaceOrder = () => {
    const { id } = useParams();
    const restaurant = restaurants.find((r) => r.menu.some((item) => item.id === parseInt(id)));
    const selectedItem = restaurant.menu.find((item) => item.id === parseInt(id));

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <h1 className="text-center display-4 mb-5 text-black">Place Your Order</h1>
            <div className="card bg-transparent border-0 text-black">
                <div className="card-body">
                    <h2 className="h4 card-title">Order Summary</h2>
                    <p className="card-text">
                        Your selected items will appear here.
                    </p>
                    {/* Add order details here */}
                    <div className="mt-4">
                        <Link to="/track" className="btn btn-light btn-block text-black">
                            Confirm Order
                        </Link>
                    </div>
                    <div className="mt-3">
                        <Link to={`/menu/${restaurant.id}`} className="btn btn-outline-light btn-block text-black">
                            Back to Menu
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PlaceOrder;
