import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const PlaceOrder = () => {
    const [cart, setCart] = useState([]);

    useEffect(() => {
        const cartItems = JSON.parse(localStorage.getItem('cart')) || [];
        setCart(cartItems);
    }, []);

    const removeFromCart = (id) => {
        const updatedCart = cart.filter(item => item.id !== id);
        setCart(updatedCart);
        localStorage.setItem('cart', JSON.stringify(updatedCart));
    };

    const restaurantId = cart.length > 0 ? cart[0].restaurantId : null;

    return (
        <div className="container py-5" style={{ backgroundColor: '#e0f7df', minHeight: '100vh' }}>
            <h1 className="text-center display-4 mb-5 text-black">Place Your Order</h1>
            <div className="card bg-transparent border-0 text-black">
                <div className="card-body">
                    <h2 className="h4 card-title">Order Summary</h2>
                    {cart.length > 0 ? (
                        <ul className="list-group">
                            {cart.map(item => (
                                <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center bg-light mb-3" style={{ borderRadius: '20px' }}>
                                    <div>
                                        <h3 className="h6">{item.name} - â‚¹{item.price}</h3>
                                        <p className="h7">Quantity: {item.quantity}</p>
                                    </div>
                                    <button onClick={() => removeFromCart(item.id)} className="btn btn-outline-danger" style={{ borderRadius: '50%' }}>
                                        X
                                    </button>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Your cart is empty.</p>
                    )}
                    <div className="mt-4">
                        <Link to="/track" className="btn btn-light btn-block text-black" style={{ borderRadius: '20px' }}>
                            Confirm Order
                        </Link>
                    </div>
                    <div className="mt-3">
                        {restaurantId && (
                            <Link to={`/menu/${restaurantId}`} className="btn btn-outline-light btn-block text-black" style={{ borderRadius: '20px' }}>
                                Back to Menu
                            </Link>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PlaceOrder;
